package sample;
import java.util.*;
public class number_in_sequence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scanner=new Scanner(System.in);
		
		int n=scanner.nextInt();
		
		for(int i=1;i<=n;i++) {
			System.out.print(i+" ");
		}

	}

}
